    public static ArrayList<Rumah> getListDAta() {
        Rumah rumah = null;
        ArrayList<Rumah> list = new ArrayList<>();
        for (int i = 0; i < data.length; i++) {
            rumah = new Rumah();
            rumah.setName(data[i][0]);
            rumah.setDeskripsi(data[i][1]);
            rumah.setAsal(data[i][2]);
            rumah.setFoto(data[i][3]);
            rumah.setUpdate(data[i][4]);

            list.add(rumah);

        }
        return list;

    }
